import React, { createContext, useContext, useReducer, ReactNode } from 'react';
import { v4 as uuidv4 } from 'uuid';
import { KanbanState, Task } from '../types/types';

// Estado inicial do Kanban
const defaultState: KanbanState = {
  tasks: {},
  columns: {
    'column-1': {
      id: 'column-1',
      title: 'A fazer',
      taskIds: [],
    },
    'column-2': {
      id: 'column-2',
      title: 'Em andamento',
      taskIds: [],
    },
    'column-3': {
      id: 'column-3',
      title: 'Concluído',
      taskIds: [],
    },
  },
  columnOrder: ['column-1', 'column-2', 'column-3'],
};

// Carregar estado do localStorage ou usar o padrão
const loadFromLocalStorage = (): KanbanState => {
  try {
    const savedState = localStorage.getItem('kanbanState');
    if (savedState) {
      return JSON.parse(savedState);
    }
  } catch (error) {
    console.error('Erro ao carregar estado do localStorage:', error);
  }
  return defaultState;
};

const initialState = loadFromLocalStorage();

// Tipos de ações
type KanbanAction =
  | { type: 'ADD_TASK'; payload: { columnId: string; content: string; description?: string; priority?: 'baixa' | 'média' | 'alta', dueDate?: Date, tags?: string[] } }
  | { type: 'MOVE_TASK'; payload: { source: { columnId: string, index: number }, destination: { columnId: string, index: number } } }
  | { type: 'UPDATE_TASK'; payload: { taskId: string; content?: string; description?: string; priority?: 'baixa' | 'média' | 'alta', dueDate?: Date, tags?: string[] } }
  | { type: 'DELETE_TASK'; payload: { taskId: string; columnId: string } }
  | { type: 'ADD_COLUMN'; payload: { title: string } }
  | { type: 'UPDATE_COLUMN'; payload: { columnId: string; title: string } }
  | { type: 'DELETE_COLUMN'; payload: { columnId: string } }
  | { type: 'ADD_TAG'; payload: { taskId: string; tag: string } }
  | { type: 'REMOVE_TAG'; payload: { taskId: string; tag: string } }
  | { type: 'EXPORT_DATA'; }
  | { type: 'IMPORT_DATA'; payload: { data: KanbanState } };

// Reducer para gerenciar o estado
const kanbanReducer = (state: KanbanState, action: KanbanAction): KanbanState => {
  let newState = state;

  switch (action.type) {
    case 'ADD_TASK': {
      const { columnId, content, description, priority, dueDate, tags } = action.payload;
      const taskId = uuidv4();
      const newTask: Task = {
        id: taskId,
        content,
        description,
        priority,
        dueDate,
        tags: tags || [],
      };

      const column = state.columns[columnId];
      const newTaskIds = [...column.taskIds, taskId];

      newState = {
        ...state,
        tasks: {
          ...state.tasks,
          [taskId]: newTask,
        },
        columns: {
          ...state.columns,
          [columnId]: {
            ...column,
            taskIds: newTaskIds,
          },
        },
      };
      break;
    }

    case 'MOVE_TASK': {
      const { source, destination } = action.payload;
      
      // Se não houver destino, não faz nada
      if (!destination) {
        return state;
      }

      // Se a origem e o destino forem iguais, não faz nada
      if (
        source.columnId === destination.columnId &&
        source.index === destination.index
      ) {
        return state;
      }

      const sourceColumn = state.columns[source.columnId];
      const destinationColumn = state.columns[destination.columnId];
      
      // Mover na mesma coluna
      if (source.columnId === destination.columnId) {
        const newTaskIds = [...sourceColumn.taskIds];
        const [removed] = newTaskIds.splice(source.index, 1);
        newTaskIds.splice(destination.index, 0, removed);

        newState = {
          ...state,
          columns: {
            ...state.columns,
            [source.columnId]: {
              ...sourceColumn,
              taskIds: newTaskIds,
            },
          },
        };
      } else {
        // Mover entre colunas diferentes
        const sourceTaskIds = [...sourceColumn.taskIds];
        const [removed] = sourceTaskIds.splice(source.index, 1);
        
        const destinationTaskIds = [...destinationColumn.taskIds];
        destinationTaskIds.splice(destination.index, 0, removed);

        newState = {
          ...state,
          columns: {
            ...state.columns,
            [source.columnId]: {
              ...sourceColumn,
              taskIds: sourceTaskIds,
            },
            [destination.columnId]: {
              ...destinationColumn,
              taskIds: destinationTaskIds,
            },
          },
        };
      }
      break;
    }

    case 'UPDATE_TASK': {
      const { taskId, content, description, priority, dueDate, tags } = action.payload;
      
      newState = {
        ...state,
        tasks: {
          ...state.tasks,
          [taskId]: {
            ...state.tasks[taskId],
            ...(content !== undefined && { content }),
            ...(description !== undefined && { description }),
            ...(priority !== undefined && { priority }),
            ...(dueDate !== undefined && { dueDate }),
            ...(tags !== undefined && { tags }),
          },
        },
      };
      break;
    }

    case 'DELETE_TASK': {
      const { taskId, columnId } = action.payload;
      const column = state.columns[columnId];
      const newTaskIds = column.taskIds.filter(id => id !== taskId);
      
      const { [taskId]: _, ...remainingTasks } = state.tasks;

      newState = {
        ...state,
        tasks: remainingTasks,
        columns: {
          ...state.columns,
          [columnId]: {
            ...column,
            taskIds: newTaskIds,
          },
        },
      };
      break;
    }

    case 'ADD_COLUMN': {
      const { title } = action.payload;
      const newColumnId = uuidv4();
      
      newState = {
        ...state,
        columns: {
          ...state.columns,
          [newColumnId]: {
            id: newColumnId,
            title,
            taskIds: [],
          },
        },
        columnOrder: [...state.columnOrder, newColumnId],
      };
      break;
    }

    case 'UPDATE_COLUMN': {
      const { columnId, title } = action.payload;
      
      newState = {
        ...state,
        columns: {
          ...state.columns,
          [columnId]: {
            ...state.columns[columnId],
            title,
          },
        },
      };
      break;
    }

    case 'DELETE_COLUMN': {
      const { columnId } = action.payload;
      const column = state.columns[columnId];
      
      // Remover todas as tarefas da coluna
      const newTasks = { ...state.tasks };
      column.taskIds.forEach(taskId => {
        delete newTasks[taskId];
      });
      
      const { [columnId]: _, ...remainingColumns } = state.columns;
      const newColumnOrder = state.columnOrder.filter(id => id !== columnId);

      newState = {
        ...state,
        tasks: newTasks,
        columns: remainingColumns,
        columnOrder: newColumnOrder,
      };
      break;
    }

    case 'ADD_TAG': {
      const { taskId, tag } = action.payload;
      const task = state.tasks[taskId];
      const existingTags = task.tags || [];
      
      if (!existingTags.includes(tag)) {
        newState = {
          ...state,
          tasks: {
            ...state.tasks,
            [taskId]: {
              ...task,
              tags: [...existingTags, tag],
            },
          },
        };
      } else {
        return state;
      }
      break;
    }

    case 'REMOVE_TAG': {
      const { taskId, tag } = action.payload;
      const task = state.tasks[taskId];
      const existingTags = task.tags || [];
      
      newState = {
        ...state,
        tasks: {
          ...state.tasks,
          [taskId]: {
            ...task,
            tags: existingTags.filter(t => t !== tag),
          },
        },
      };
      break;
    }

    case 'IMPORT_DATA': {
      newState = action.payload.data;
      break;
    }

    default:
      return state;
  }

  // Salvar no localStorage após cada ação
  try {
    localStorage.setItem('kanbanState', JSON.stringify(newState));
  } catch (error) {
    console.error('Erro ao salvar estado no localStorage:', error);
  }

  return newState;
};

// Interface do contexto
interface KanbanContextProps {
  state: KanbanState;
  dispatch: React.Dispatch<KanbanAction>;
  exportData: () => void;
  importData: (data: string) => void;
}

// Criação do contexto
const KanbanContext = createContext<KanbanContextProps | undefined>(undefined);

// Provider component
interface KanbanProviderProps {
  children: ReactNode;
}

export const KanbanProvider: React.FC<KanbanProviderProps> = ({ children }) => {
  const [state, dispatch] = useReducer(kanbanReducer, initialState);

  const exportData = () => {
    const dataStr = JSON.stringify(state, null, 2);
    const dataUri = `data:application/json;charset=utf-8,${encodeURIComponent(dataStr)}`;
    
    // Criar um link temporário para download
    const exportFileDefaultName = `kanban-backup-${new Date().toISOString().slice(0, 10)}.json`;
    const linkElement = document.createElement('a');
    linkElement.setAttribute('href', dataUri);
    linkElement.setAttribute('download', exportFileDefaultName);
    linkElement.click();
  };

  const importData = (jsonData: string) => {
    try {
      const data = JSON.parse(jsonData) as KanbanState;
      dispatch({
        type: 'IMPORT_DATA',
        payload: { data },
      });
    } catch (error) {
      console.error('Erro ao importar dados:', error);
      alert('Formato de arquivo inválido. Por favor, selecione um arquivo JSON válido.');
    }
  };

  return (
    <KanbanContext.Provider value={{ state, dispatch, exportData, importData }}>
      {children}
    </KanbanContext.Provider>
  );
};

// Hook para usar o contexto
export const useKanban = (): KanbanContextProps => {
  const context = useContext(KanbanContext);
  if (!context) {
    throw new Error('useKanban deve ser usado dentro de um KanbanProvider');
  }
  return context;
};
