import React from 'react';
import { Draggable } from 'react-beautiful-dnd';
import { Card, CardContent, Typography, Box, Chip, IconButton } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import { Task as TaskType } from '../types/types';
import { useKanban } from '../context/KanbanContext';

interface TaskProps {
  task: TaskType;
  index: number;
  columnId: string;
  onEdit: (task: TaskType) => void;
}

const priorityColors = {
  'baixa': '#4caf50', // Verde
  'média': '#ff9800', // Laranja
  'alta': '#f44336', // Vermelho
};

const Task: React.FC<TaskProps> = ({ task, index, columnId, onEdit }) => {
  const { dispatch } = useKanban();

  const handleDelete = () => {
    dispatch({
      type: 'DELETE_TASK',
      payload: { taskId: task.id, columnId },
    });
  };

  const formatDate = (date: Date | undefined) => {
    if (!date) return '';
    return new Date(date).toLocaleDateString('pt-BR');
  };

  return (
    <Draggable draggableId={task.id} index={index}>
      {(provided, snapshot) => (
        <Card
          ref={provided.innerRef}
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          sx={{
            marginBottom: 2,
            backgroundColor: snapshot.isDragging ? '#f5f5f5' : 'white',
            boxShadow: snapshot.isDragging ? 3 : 1,
          }}
        >
          <CardContent>
            <Box display="flex" justifyContent="space-between" alignItems="flex-start">
              <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
                {task.content}
              </Typography>
              <Box>
                <IconButton size="small" onClick={() => onEdit(task)}>
                  <EditIcon fontSize="small" />
                </IconButton>
                <IconButton size="small" onClick={handleDelete}>
                  <DeleteIcon fontSize="small" />
                </IconButton>
              </Box>
            </Box>
            
            {task.description && (
              <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                {task.description}
              </Typography>
            )}
            
            <Box display="flex" gap={1} flexWrap="wrap" mt={1}>
              {task.priority && (
                <Chip 
                  label={`Prioridade: ${task.priority}`} 
                  size="small" 
                  sx={{ 
                    backgroundColor: priorityColors[task.priority],
                    color: 'white',
                  }} 
                />
              )}
              
              {task.dueDate && (
                <Chip 
                  label={`Prazo: ${formatDate(task.dueDate)}`} 
                  size="small" 
                  color="primary" 
                  variant="outlined" 
                />
              )}
              
              {/* Tags da tarefa */}
              {task.tags && task.tags.length > 0 && (
                <Box display="flex" gap={0.5} flexWrap="wrap" mt={0.5} width="100%">
                  {task.tags.map(tag => (
                    <Chip
                      key={tag}
                      label={tag}
                      size="small"
                      variant="outlined"
                      color="secondary"
                      sx={{ fontSize: '0.7rem', height: '20px' }}
                    />
                  ))}
                </Box>
              )}
            </Box>
          </CardContent>
        </Card>
      )}
    </Draggable>
  );
};

export default Task;
