import React, { useState, useMemo } from 'react';
import { DragDropContext, DropResult } from 'react-beautiful-dnd';
import { Box, Button, TextField, Dialog, DialogTitle, DialogContent, DialogActions, Container } from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import Column from './Column';
import KanbanFilters from './KanbanFilters';
import { useKanban } from '../context/KanbanContext';
import { Task } from '../types/types';

const KanbanBoard: React.FC = () => {
  const { state, dispatch } = useKanban();
  const [isAddingColumn, setIsAddingColumn] = useState(false);
  const [newColumnTitle, setNewColumnTitle] = useState('');
  
  // Filtros
  const [textFilter, setTextFilter] = useState('');
  const [priorityFilter, setPriorityFilter] = useState('');
  const [tagFilters, setTagFilters] = useState<string[]>([]);

  // Filtragem de tarefas
  const filteredTasks = useMemo(() => {
    const result: { [key: string]: Task } = {};
    
    Object.entries(state.tasks).forEach(([id, task]) => {
      // Filtro de texto
      const matchesText = textFilter === '' || 
        task.content.toLowerCase().includes(textFilter.toLowerCase()) ||
        (task.description && task.description.toLowerCase().includes(textFilter.toLowerCase()));
      
      // Filtro de prioridade
      const matchesPriority = priorityFilter === '' || task.priority === priorityFilter;
      
      // Filtro de tags
      const matchesTags = tagFilters.length === 0 || 
        (task.tags && tagFilters.every(tag => task.tags!.includes(tag)));
      
      if (matchesText && matchesPriority && matchesTags) {
        result[id] = task;
      }
    });
    
    return result;
  }, [state.tasks, textFilter, priorityFilter, tagFilters]);

  const handleDragEnd = (result: DropResult) => {
    const { destination, source, draggableId, type } = result;

    // Se não houver destino ou se o item for solto na mesma posição
    if (!destination || (destination.droppableId === source.droppableId && destination.index === source.index)) {
      return;
    }

    // Movimentação de tarefas
    dispatch({
      type: 'MOVE_TASK',
      payload: {
        source: {
          columnId: source.droppableId,
          index: source.index,
        },
        destination: {
          columnId: destination.droppableId,
          index: destination.index,
        },
      },
    });
  };

  const handleAddColumn = () => {
    if (newColumnTitle.trim()) {
      dispatch({
        type: 'ADD_COLUMN',
        payload: {
          title: newColumnTitle,
        },
      });
      setNewColumnTitle('');
      setIsAddingColumn(false);
    }
  };

  return (
    <Container maxWidth={false} disableGutters sx={{ height: '100%', display: 'flex', flexDirection: 'column', p: 2 }}>
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          mb: 2,
        }}
      >
        <h1>Quadro Kanban</h1>
        <Button
          variant="contained"
          startIcon={<AddIcon />}
          onClick={() => setIsAddingColumn(true)}
        >
          Nova Coluna
        </Button>
      </Box>

      <KanbanFilters
        textFilter={textFilter}
        setTextFilter={setTextFilter}
        priorityFilter={priorityFilter}
        setPriorityFilter={setPriorityFilter}
        tagFilters={tagFilters}
        setTagFilters={setTagFilters}
      />

      <DragDropContext onDragEnd={handleDragEnd}>
        <Box
          sx={{
            display: 'flex',
            overflowX: 'auto',
            flexGrow: 1,
            gap: 2,
            backgroundColor: '#e0e0e0',
            p: 2,
            borderRadius: 1,
            height: 'calc(100vh - 200px)',
          }}
        >
          {state.columnOrder.map((columnId) => {
            const column = state.columns[columnId];
            // Filtrar as tarefas para cada coluna
            const columnTasks = column.taskIds
              .filter(taskId => filteredTasks[taskId])
              .map(taskId => filteredTasks[taskId]);

            return <Column key={column.id} column={column} tasks={columnTasks} />;
          })}
        </Box>
      </DragDropContext>

      <Dialog open={isAddingColumn} onClose={() => setIsAddingColumn(false)}>
        <DialogTitle>Nova Coluna</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Título da Coluna"
            fullWidth
            value={newColumnTitle}
            onChange={(e) => setNewColumnTitle(e.target.value)}
            onKeyPress={(e) => {
              if (e.key === 'Enter') {
                handleAddColumn();
              }
            }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setIsAddingColumn(false)}>Cancelar</Button>
          <Button onClick={handleAddColumn} disabled={!newColumnTitle.trim()} variant="contained">
            Adicionar
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default KanbanBoard;
