import React, { useState } from 'react';
import { Droppable } from 'react-beautiful-dnd';
import { Paper, Typography, Button, Box, TextField, IconButton } from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import Task from './Task';
import { Column as ColumnType, Task as TaskType } from '../types/types';
import { useKanban } from '../context/KanbanContext';
import TaskFormDialog from './TaskFormDialog';

interface ColumnProps {
  column: ColumnType;
  tasks: TaskType[];
}

const Column: React.FC<ColumnProps> = ({ column, tasks }) => {
  const { dispatch } = useKanban();
  const [isAddingTask, setIsAddingTask] = useState(false);
  const [isEditingColumn, setIsEditingColumn] = useState(false);
  const [columnTitle, setColumnTitle] = useState(column.title);
  const [newTaskContent, setNewTaskContent] = useState('');
  const [taskFormOpen, setTaskFormOpen] = useState(false);
  const [currentTask, setCurrentTask] = useState<TaskType | null>(null);

  const handleAddTask = () => {
    if (newTaskContent.trim()) {
      dispatch({
        type: 'ADD_TASK',
        payload: {
          columnId: column.id,
          content: newTaskContent,
        },
      });
      setNewTaskContent('');
      setIsAddingTask(false);
    }
  };

  const handleColumnUpdate = () => {
    if (columnTitle.trim()) {
      dispatch({
        type: 'UPDATE_COLUMN',
        payload: {
          columnId: column.id,
          title: columnTitle,
        },
      });
      setIsEditingColumn(false);
    }
  };

  const handleDeleteColumn = () => {
    if (window.confirm(`Tem certeza que deseja excluir a coluna "${column.title}"?`)) {
      dispatch({
        type: 'DELETE_COLUMN',
        payload: {
          columnId: column.id,
        },
      });
    }
  };

  const handleOpenTaskForm = () => {
    setCurrentTask(null);
    setTaskFormOpen(true);
  };

  const handleEditTask = (task: TaskType) => {
    setCurrentTask(task);
    setTaskFormOpen(true);
  };

  const handleCloseTaskForm = () => {
    setTaskFormOpen(false);
  };

  return (
    <Paper
      sx={{
        minWidth: 280,
        maxWidth: 350,
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
        backgroundColor: '#f5f5f5',
        pb: 2,
      }}
    >
      <Box
        p={2}
        display="flex"
        justifyContent="space-between"
        alignItems="center"
        bgcolor="primary.main"
        color="white"
        borderRadius="4px 4px 0 0"
      >
        {isEditingColumn ? (
          <TextField
            value={columnTitle}
            onChange={(e) => setColumnTitle(e.target.value)}
            variant="outlined"
            size="small"
            autoFocus
            fullWidth
            InputProps={{
              sx: { color: 'white', '& .MuiOutlinedInput-notchedOutline': { borderColor: 'white' } }
            }}
            onBlur={handleColumnUpdate}
            onKeyPress={(e) => {
              if (e.key === 'Enter') {
                handleColumnUpdate();
              }
            }}
          />
        ) : (
          <Typography variant="h6">{column.title}</Typography>
        )}
        <Box>
          <IconButton size="small" onClick={() => setIsEditingColumn(true)} sx={{ color: 'white' }}>
            <EditIcon fontSize="small" />
          </IconButton>
          <IconButton size="small" onClick={handleDeleteColumn} sx={{ color: 'white' }}>
            <DeleteIcon fontSize="small" />
          </IconButton>
        </Box>
      </Box>

      <Droppable droppableId={column.id}>
        {(provided) => (
          <Box
            ref={provided.innerRef}
            {...provided.droppableProps}
            sx={{
              flex: 1,
              minHeight: 100,
              p: 2,
              overflow: 'auto',
            }}
          >
            {tasks.map((task, index) => (
              <Task
                key={task.id}
                task={task}
                index={index}
                columnId={column.id}
                onEdit={handleEditTask}
              />
            ))}
            {provided.placeholder}
          </Box>
        )}
      </Droppable>

      {isAddingTask ? (
        <Box p={2}>
          <TextField
            fullWidth
            size="small"
            placeholder="Digite o tÃ­tulo da tarefa"
            value={newTaskContent}
            onChange={(e) => setNewTaskContent(e.target.value)}
            autoFocus
            onBlur={() => {
              if (!newTaskContent.trim()) {
                setIsAddingTask(false);
              }
            }}
            onKeyPress={(e) => {
              if (e.key === 'Enter') {
                handleAddTask();
              }
            }}
            sx={{ mb: 1 }}
          />
          <Box display="flex" gap={1}>
            <Button
              variant="contained"
              size="small"
              onClick={handleAddTask}
              disabled={!newTaskContent.trim()}
            >
              Adicionar
            </Button>
            <Button
              variant="outlined"
              size="small"
              onClick={() => setIsAddingTask(false)}
            >
              Cancelar
            </Button>
          </Box>
        </Box>
      ) : (
        <Box p={2} pt={0} display="flex" gap={1}>
          <Button
            variant="contained"
            startIcon={<AddIcon />}
            onClick={() => setIsAddingTask(true)}
            fullWidth
            size="small"
          >
            Adicionar Tarefa
          </Button>
          <Button
            variant="outlined"
            startIcon={<AddIcon />}
            onClick={handleOpenTaskForm}
            fullWidth
            size="small"
          >
            Detalhada
          </Button>
        </Box>
      )}
      
      <TaskFormDialog
        open={taskFormOpen}
        onClose={handleCloseTaskForm}
        task={currentTask}
        columnId={column.id}
      />
    </Paper>
  );
};

export default Column;
