import React, { useState, useEffect } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Box,
  Chip,
  Stack,
  Autocomplete,
} from '@mui/material';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { Task } from '../types/types';
import { useKanban } from '../context/KanbanContext';

interface TaskFormDialogProps {
  open: boolean;
  onClose: () => void;
  task: Task | null;
  columnId: string;
}

const TaskFormDialog: React.FC<TaskFormDialogProps> = ({
  open,
  onClose,
  task,
  columnId,
}) => {
  const { state, dispatch } = useKanban();
  const [content, setContent] = useState('');
  const [description, setDescription] = useState('');
  const [priority, setPriority] = useState<'baixa' | 'média' | 'alta' | ''>('');
  const [dueDate, setDueDate] = useState<Date | null>(null);
  const [tags, setTags] = useState<string[]>([]);
  const [newTag, setNewTag] = useState('');

  // Coletar todas as tags existentes para sugestões
  const allTags = Array.from(
    new Set(
      Object.values(state.tasks).flatMap(task => task.tags || [])
    )
  ).sort();

  useEffect(() => {
    if (task) {
      setContent(task.content);
      setDescription(task.description || '');
      setPriority(task.priority || '');
      setDueDate(task.dueDate ? new Date(task.dueDate) : null);
      setTags(task.tags || []);
    } else {
      setContent('');
      setDescription('');
      setPriority('');
      setDueDate(null);
      setTags([]);
    }
  }, [task]);

  const handleAddTag = () => {
    if (newTag.trim() && !tags.includes(newTag.trim())) {
      setTags([...tags, newTag.trim()]);
      setNewTag('');
    }
  };

  const handleDeleteTag = (tagToDelete: string) => {
    setTags(tags.filter(tag => tag !== tagToDelete));
  };

  const handleSubmit = () => {
    if (content.trim()) {
      if (task) {
        // Editar tarefa existente
        dispatch({
          type: 'UPDATE_TASK',
          payload: {
            taskId: task.id,
            content,
            description: description || undefined,
            priority: priority || undefined,
            dueDate: dueDate || undefined,
            tags,
          },
        });
      } else {
        // Adicionar nova tarefa
        dispatch({
          type: 'ADD_TASK',
          payload: {
            columnId,
            content,
            description: description || undefined,
            priority: priority || undefined,
            dueDate: dueDate || undefined,
            tags,
          },
        });
      }
      onClose();
    }
  };

  // Função para formatar a data no formato brasileiro
  const formatDateToBR = (date: Date | null) => {
    if (!date) return '';
    const day = date.getDate().toString().padStart(2, '0');
    const month = (date.getMonth() + 1).toString().padStart(2, '0');
    const year = date.getFullYear();
    return `${day}/${month}/${year}`;
  };

  return (
    <Dialog open={open} onClose={onClose} maxWidth="sm" fullWidth>
      <DialogTitle>{task ? 'Editar Tarefa' : 'Nova Tarefa'}</DialogTitle>
      <DialogContent>
        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, pt: 1 }}>
          <TextField
            label="Título"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            fullWidth
            required
            autoFocus
          />
          <TextField
            label="Descrição"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            fullWidth
            multiline
            rows={3}
          />
          <FormControl fullWidth>
            <InputLabel id="priority-label">Prioridade</InputLabel>
            <Select
              labelId="priority-label"
              value={priority}
              label="Prioridade"
              onChange={(e) => setPriority(e.target.value as 'baixa' | 'média' | 'alta' | '')}
            >
              <MenuItem value="">
                <em>Nenhuma</em>
              </MenuItem>
              <MenuItem value="baixa">Baixa</MenuItem>
              <MenuItem value="média">Média</MenuItem>
              <MenuItem value="alta">Alta</MenuItem>
            </Select>
          </FormControl>
          <LocalizationProvider dateAdapter={AdapterDateFns}>
            <DatePicker
              label="Data de vencimento"
              value={dueDate}
              onChange={(newValue: Date | null) => setDueDate(newValue)}
              slotProps={{ textField: { fullWidth: true } }}
              format="dd/MM/yyyy"
            />
          </LocalizationProvider>

          {/* Gestão de etiquetas */}
          <Box>
            <Autocomplete
              value={newTag}
              onChange={(event, newValue) => {
                setNewTag(newValue || '');
              }}
              inputValue={newTag}
              onInputChange={(event, newInputValue) => {
                setNewTag(newInputValue);
              }}
              options={allTags.filter(tag => !tags.includes(tag))}
              freeSolo
              renderInput={(params) => (
                <TextField 
                  {...params} 
                  label="Adicionar etiqueta" 
                  fullWidth
                  onKeyPress={(e) => {
                    if (e.key === 'Enter') {
                      e.preventDefault();
                      handleAddTag();
                    }
                  }}
                />
              )}
            />
            <Button 
              onClick={handleAddTag} 
              variant="outlined" 
              size="small" 
              sx={{ mt: 1, mb: 2 }}
              disabled={!newTag.trim() || tags.includes(newTag.trim())}
            >
              Adicionar Etiqueta
            </Button>

            <Stack direction="row" spacing={1} flexWrap="wrap" useFlexGap>
              {tags.map((tag) => (
                <Chip
                  key={tag}
                  label={tag}
                  onDelete={() => handleDeleteTag(tag)}
                  color="primary"
                  variant="outlined"
                  size="small"
                  sx={{ mb: 1 }}
                />
              ))}
            </Stack>
          </Box>
        </Box>
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Cancelar</Button>
        <Button
          onClick={handleSubmit}
          variant="contained"
          color="primary"
          disabled={!content.trim()}
        >
          {task ? 'Atualizar' : 'Adicionar'}
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default TaskFormDialog;
